package com.ankit.leetcode;

import java.util.Arrays;

/* 389. Find the Difference
* You are given two strings s and t.
* String t is generated by random shuffling string s and then add one more letter at a random position.
* Return the letter that was added to t.
*
* Input: s = "abcd", t = "abcde" -> Output: "e"
* Input: s = "", t = "y" -> Output: "y"
 */
public class FindDifference {

  public static void main(String[] args) {
    FindDifference rv = new FindDifference();
    String s = "";
    String t = "";     //t is formed by shuffling s and added a letter at a random position

    System.out.println(rv.findDifferenceAndAddedWord(s, t));
  }

  public char findDifferenceAndAddedWord(String s, String t) {
    char[] sArray = s.toCharArray();
    Arrays.sort(sArray);
    System.out.println(sArray);

    char[] tArray =t.toCharArray();
    Arrays.sort(tArray);
    System.out.println(tArray);

    for(int i=0; i<sArray.length; i++) {
      if(tArray[i] != sArray[i]) {
        return tArray[i];
      }
    }
    return tArray[sArray.length];
  }

}
